INSERT INTO `psd_task` VALUES (160, '6764988b-5058-46d9-82b9-159538053981', NULL, '测试1', '测试1', NULL, NULL, '{\"copywriter\":\"\\n【新生儿取名绝了！这个自带书香的名字居然出自朱熹名句】\\n\\n✨《观书有感》里的神仙名字被我挖到了！  \\n朱熹笔下的\\\"问渠那得清如许\\\"  \\n藏着这个自带书卷气的绝美三字名——  \\n「清如许」  \\n\\n▫️出处硬核：宋代大儒朱熹经典名句  \\n▫️音韵清越：三字平仄相间朗朗上口  \\n▫️适配百搭：配苏/林/江等姓氏都超惊艳  \\n▫️意境满分：既有流水清澈的灵动感  \\n又暗含\\\"活水源头\\\"的智慧哲学  \\n▫️性别通用：男女宝宝都能用的中性美名  \\n\\n❤️敲黑板划重点：  \\n1️⃣避开生僻字又能彰显文化底蕴  \\n2️⃣三字结构平衡不撞款  \\n3️⃣适配现代80/90后父母审美  \\n\\n最近在《典籍里的中国》又听到这句诗  \\n突然发现用作人名简直封神！  \\n配古风汉服照或现代日常装都超有氛围  \\n\\n↓ ↓ ↓  \\n正在纠结宝宝名字的准爸妈快收好  \\n这个把江南水乡装进名字里的神仙配置  \\n自带国学滤镜还能写进族谱！  \\n\\n#新生儿取名 #诗词取名 #宝宝起名攻略  \\n#清如许 #国学经典  \\n\\n（评论区晒出你家崽的神仙名字，揪三位送手写诗词名帖～）\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"清如许1\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"【清如许】名字解析｜宋韵国风DNA动了\\n◽️取自朱熹『问渠那得清如许』的诗意\\n◽️自带清泉流响的山水画氛围感\\n◽️比奶茶三分糖更清冽的高级感\"}}},{\"folderName\":\"封面\",\"generateCount\":\"1\",\"hasSubfolder\":false,\"subfolderName\":\"\",\"textLayerConfigs\":{\"textLayer3\":{\"maxCharsPerLine\":\"6\",\"name\":\"封面\",\"sampleText\":\"时光褶皱里的未寄信笺2\"}}}],\"baseConfig\":{\"accountName\":\"测试1\",\"psdLocalPath\":\"C:\\\\Users\\\\liuxianying\\\\Downloads\\\\A4xin.psd\",\"imageSavePath\":\"C:\\\\output\",\"templateName\":\"测试1\"}}', '1', NULL, '2025-04-16 10:31:55');
INSERT INTO `psd_task` VALUES (161, '715c09cb-15cc-410e-bc9c-26dd0a011d64', NULL, '测试1', '测试1', NULL, NULL, '{\"copywriter\":\"\\n\\n📚新生儿取名攻略｜这个一眼万年的名字我夸爆！\\n\\n最近给闺蜜宝宝取的\\\"远之辰\\\"被全家夸爆啦！\\n新手爸妈取名必备这3招👇🏻\\n\\n🌈【音韵搭配法】\\n姓+名要避免\\\"绕口令式\\\"发音\\n远之辰三字平仄相间\\n念起来如春风拂面般舒服\\n\\n📖【引经据典法】\\n取自《楚辞》\\\"辰极星远\\\"\\n既有文化底蕴又充满诗意\\n寓意宝宝如星辰般璀璨✨\\n\\n💡【意象延伸法】\\n\\\"远\\\"象征前程似锦\\n\\\"辰\\\"暗含良辰美景\\n搭配起来满满都是对宝宝的期许\\n\\n⚠️避雷指南：\\n✖️生僻字影响社交\\n✖️谐音梗易被取笑\\n✖️笔画太多难书写\\n\\n小tips：\\n取名时多念几遍\\n结合生辰八字更佳哦！\\n\\n你家宝宝叫什么名字呀？\\n欢迎在评论区交流取名心得❤️\\n\\n#宝宝取名 #新生儿名字推荐 #远之辰 #取名攻略 #孕期日记\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"远之辰\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"杨本身是一种树木，拆解为木易二字，结合姓氏字形，音律都为上佳，也有祝福孩子茁壮成长为参天大树，顺风顺水之意。\"}}},{\"folderName\":\"封面\",\"generateCount\":\"1\",\"hasSubfolder\":false,\"subfolderName\":\"\",\"textLayerConfigs\":{\"textLayer3\":{\"maxCharsPerLine\":\"6\",\"name\":\"封面\",\"sampleText\":\"时光褶皱里的数字回声\"}}}],\"baseConfig\":{\"accountName\":\"测试1\",\"psdLocalPath\":\"C:\\\\Users\\\\liuxianying\\\\Downloads\\\\A4xin.psd\",\"imageSavePath\":\"C:\\\\output\",\"templateName\":\"测试1\"}}', '0', NULL, '2025-04-16 11:51:16');
INSERT INTO `psd_task` VALUES (162, '84bd8e30-8f05-42ca-bbfc-531050053c41', NULL, '测试1', '测试1', NULL, NULL, '{\"copywriter\":\"\\\\n\\\\n✨【新生儿取名避坑指南】新手爸妈必看的绝美名库✨\\\\n\\\\n「逸风尘」拆解攻略\\\\n✔️ 逸+尘=动静结合美学\\\\n「逸」取自陶渊明\\\\\"云无心以出岫\\\\\"的闲适意境\\\\n「尘」呼应\\\\\"和光同尘\\\\\"哲学\\\\n搭配风字作过渡 瞬间提升名字呼吸感\\\\n\\\\n「灵韵天成」起名公式\\\\n🌸自然意象+虚实结合\\\\n「灵韵」出自《文心雕龙》\\\\\"物色尽而情有余\\\\\"\\\\n「天成」暗含《中庸》\\\\\"天命之谓性\\\\\"思想\\\\n虚实字1:1配比 念起来如诗如歌\\\\n\\\\n🔥三大取名禁忌\\\\n🚫忌用生僻字（上户口会哭）\\\\n🚫忌谐音歧义（先念20遍）\\\\n🚫忌强行拼凑（参考古诗文更高级）\\\\n\\\\n评论区留下宝宝姓氏\\\\n帮你们定制专属诗意名哦💌\\\\n\\\\n#新生儿取名 #宝宝起名 #诗词取名 #国学文化 #取名攻略\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"逸风尘\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"灵韵天成\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"\"}}},{\"folderName\":\"封面\",\"generateCount\":\"1\",\"hasSubfolder\":false,\"subfolderName\":\"\",\"textLayerConfigs\":{\"textLayer3\":{\"maxCharsPerLine\":\"6\",\"name\":\"封面\",\"sampleText\":\"在裂缝中寻找光：破碎时代的重生叙事\"}}}],\"baseConfig\":{\"accountName\":\"测试1\",\"psdLocalPath\":\"C:\\\\\\\\Users\\\\\\\\liuxianying\\\\\\\\Downloads\\\\\\\\A4xin.psd\",\"imageSavePath\":\"C:\\\\\\\\output\",\"templateName\":\"测试1\"}}', '0', NULL, '2025-04-16 11:57:19');
INSERT INTO `psd_task` VALUES (163, '067993f4-b23e-4e75-9caf-d88dfce3b829', NULL, '测试1', '测试1', 'admin', NULL, '\"{\\\"copywriter\\\":\\\"\\\\\\\\n\\\\\\\\n🌲宝宝取名灵感库｜不撞款的清冷系名字天花板！\\\\\\\\n\\\\\\\\n「凌寒松」出处解析👇\\\\\\\\n✦取自《赠从弟》\\\\\\\\\\\"岂不罹凝寒，松柏有本性\\\\\\\\\\\"\\\\\\\\n✦巧妙化用王安石\\\\\\\\\\\"凌寒独自开\\\\\\\\\\\"意境\\\\\\\\n✦三个字自带水墨画般的清冽气质\\\\\\\\n\\\\\\\\n💡寓意拆解\\\\\\\\n「凌」➜ 凌霄之志 展翅高飞\\\\\\\\n「寒」➜ 梅傲霜雪 坚韧品性\\\\\\\\n「松」➜ 松筠之节 浩然正气\\\\\\\\n\\\\\\\\n✨适配宝宝类型\\\\\\\\n➀ 冬季出生的冰雪聪明崽\\\\\\\\n➁ 书香世家的气质萌娃\\\\\\\\n➂ 希望培养坚韧品格的宝贝\\\\\\\\n\\\\\\\\n📝取名小贴士\\\\\\\\n✓ 注意平仄搭配：双阴平+阳平声调超顺口\\\\\\\\n✓ 避免生僻字：兼顾诗意与实用性\\\\\\\\n✓ 搭配温润字辈：推荐「书/墨/砚」等字\\\\\\\\n\\\\\\\\n准爸妈快收藏这篇取名指南！点亮小❤️‍🔥解锁更多诗意好名～\\\",\\\"imageConfigs\\\":[{\\\"folderName\\\":\\\"正文靠左 三个字 无姓氏\\\",\\\"generateCount\\\":\\\"1\\\",\\\"hasSubfolder\\\":true,\\\"subfolderName\\\":\\\"4\\\",\\\"textLayerConfigs\\\":{\\\"textLayer1\\\":{\\\"maxCharsPerLine\\\":\\\"3\\\",\\\"name\\\":\\\"名字\\\",\\\"sampleText\\\":\\\"凌寒松\\\"},\\\"textLayer2\\\":{\\\"maxCharsPerLine\\\":\\\"15\\\",\\\"name\\\":\\\"正文\\\",\\\"sampleText\\\":\\\"凌寒松｜爹妈起名界的天花板！\\\\\\\\n\\\\\\\\n✨拆字解析：\\\\\\\\n「凌」字自带霸总气场\\\\\\\\n「寒」雪淬炼出清冷感\\\\\\\\n「松」是刻进DNA的坚韧\\\\\\\\n\\\\\\\\n🔥吹爆理由：\\\\\\\\n这三个字组合直接封神！\\\\\\\\n听名字就像从古风小说走出来的\\\\\\\\n隐世宗门大师兄/\\\\\\\\n金融圈冷面操盘手/\\\\\\\\n实验楼禁欲系教授\\\\\\\\n\\\\\\\\n💡人设关键词：\\\\\\\\n#破碎感与力量感的神级融合\\\\\\\\n#行走的冰川龙井香\\\\\\\\n#用最淡的语气说最拽的话\\\\\\\\n\\\\\\\\n（配图建议：雪松林侧颜杀+金丝眼镜+冷白皮暴击）\\\"}}},{\\\"folderName\\\":\\\"封面\\\",\\\"generateCount\\\":\\\"1\\\",\\\"hasSubfolder\\\":false,\\\"subfolderName\\\":\\\"\\\",\\\"textLayerConfigs\\\":{\\\"textLayer3\\\":{\\\"maxCharsPerLine\\\":\\\"6\\\",\\\"name\\\":\\\"封面\\\",\\\"sampleText\\\":\\\"时间的碎片与永恒的回声\\\"}}}],\\\"baseConfig\\\":{\\\"accountName\\\":\\\"测试1\\\",\\\"psdLocalPath\\\":\\\"C:\\\\\\\\\\\\\\\\Users\\\\\\\\\\\\\\\\liuxianying\\\\\\\\\\\\\\\\Downloads\\\\\\\\\\\\\\\\A4xin.psd\\\",\\\"imageSavePath\\\":\\\"C:\\\\\\\\\\\\\\\\output\\\",\\\"templateName\\\":\\\"测试1\\\"}}\"', '0', NULL, '2025-04-16 16:07:19');
INSERT INTO `psd_task` VALUES (164, '14fe102f-e1f2-4ff3-ba96-56927fe75e38', NULL, '测试1', '测试1', 'admin', NULL, '{\"copywriter\":\"\\n\\n【新生儿取名攻略】古风名起名公式大公开！新手爸妈速存！\\n\\n「墨染江南·云起时」  \\n最近闺蜜喜得贵子  \\n翻遍诗经楚辞终于悟出  \\n古风名三大起名法则  \\n新手爸妈直接抄作业！  \\n\\n✒️【虚实结合法】  \\n「墨云轩」就是绝佳案例！  \\n墨（实物）+云（虚景）=水墨丹青的意境  \\n这种名字自带画面感  \\n男宝女宝都能用  \\n\\n🌸【诗词嫁接术】  \\n\\\"髣髴兮若轻云之蔽月  \\n飘飖兮若流风之回雪\\\"  \\n截取「飘逸出尘」四字  \\n直接把曹植的浪漫刻进DNA  \\n\\n🎐【五行点睛法】  \\n木字旁+氵字底=生生不息  \\n推荐收藏20个百搭字：  \\n霁、潋、蘅、岫、岍  \\n泠、沄、翕、翊、珩  \\n\\n（配图：水墨风新生儿手脚特写+烫金姓名卡）  \\n\\n敲黑板！古风名避雷指南：  \\n✖️生僻字笔画多（上户口会哭）  \\n✖️谐音梗（楚樊→出糗）  \\n✔️搭配现代字更灵动（如：顾清羽）  \\n\\n评论区蹲蹲大家的宝宝名字  \\n揪三位送定制诗词取名方案！  \\n\\n#宝宝取名 #古风名字 #新生儿 #孕期日记 #起名攻略\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"墨云轩\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"杨本身是一种树木，拆解为木易二字，结合姓氏字形，音律都为上佳，也有祝福孩子茁壮成长为参天大树，顺风顺水之意。\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"飘逸出尘古风名\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"杨本身是一种树木，拆解为木易二字，结合姓氏字形，音律都为上佳，也有祝福孩子茁壮成长为参天大树，顺风顺水之意。\"}}},{\"folderName\":\"封面\",\"generateCount\":\"1\",\"hasSubfolder\":false,\"subfolderName\":\"\",\"textLayerConfigs\":{\"textLayer3\":{\"maxCharsPerLine\":\"6\",\"name\":\"标题\",\"sampleText\":\"在时间的裂缝中拾光\"}}}],\"baseConfig\":{\"accountName\":\"测试1\",\"psdLocalPath\":\"C:\\\\Users\\\\liuxianying\\\\Downloads\\\\A4xin.psd\",\"imageSavePath\":\"C:\\\\output\",\"templateName\":\"测试1\"}}', '0', NULL, '2025-04-16 16:27:53');
