INSERT INTO `psd_task` VALUES (169, '5265fb0a-2469-4a3c-9cb2-ed8b9effcd7c', NULL, '测试1', '测试1', 'admin', NULL, '{\"copywriter\":\"\\\\n\\\\n【在裂缝中生长的光｜新生儿取名玄学指南】✨\\\\n\\\\n宝妈们看过来！这些藏在诗画里的名字\\\\n自带逆光生长的治愈力👇\\\\n\\\\n🌸「清和」——取自《兰亭集序》\\\\n\\\\\"天朗气清，惠风和畅\\\\\"\\\\n像春日晒过阳光的襁褓\\\\n适合温润如玉的男宝\\\\n\\\\n🌿「景明」——典出《岳阳楼记》\\\\n\\\\\"至若春和景明\\\\\"\\\\n自带破晓时分的希望感\\\\n给早产宝宝加倍的生命力\\\\n\\\\n☁️「云舒」——源于洪应明佳句\\\\n\\\\\"宠辱不惊，看庭前花开花落\\\\\"\\\\n如柔软云朵包裹脆弱\\\\n给NICU宝宝最好的守护\\\\n\\\\n🌊「静澜」——化用《文心雕龙》\\\\n\\\\\"水停以鉴，火静而朗\\\\\"\\\\n暗含风浪后的平静智慧\\\\n适合历经保胎的坚强宝贝\\\\n\\\\n💧「若水」——老子《道德经》\\\\n\\\\\"上善若水，水利万物\\\\\"\\\\n像穿透石缝的涓涓细流\\\\n给先天不足宝宝温柔力量\\\\n\\\\n【取名三原则】\\\\n1️⃣引自然意象：破晓/云霞/流水\\\\n2️⃣化国学经典：四书五经最优\\\\n3️⃣重声韵美学：平仄交替如歌谣\\\\n\\\\n每个在困境中诞生的孩子\\\\n都该拥有照亮生命的名字🌟\\\\n#新生儿取名 #诗经取名 #母婴玄学 #治愈系名字\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"清和\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"『清』如晨露初霁，『和』似春风拂面！\\\\n✨氧气感爆棚的温柔天花板！\\\\n🍃人设：不食烟火的白月光\\\\n🔥吹爆点：自带初恋滤镜/名字界刘亦菲/一听就想送情书！\\\\n#干净美学 #氛围感姓氏 #起名玄学\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"景明\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"『景』是人间四月天，『明』为眸中星河闪！\\\\n💥氛围感直接拉满的国风顶流！\\\\n☀️人设：古偶在逃男主\\\\n🔥吹爆点：光念名字就心动/校园文男主模版/出片率200%！\\\\n#新中式起名 #小说照进现实 #神仙id\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"云舒\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"『云』卷九霄任逍遥，『舒』展眉间万般妙！\\\\n💫人间清醒大女主名号申请出战！\\\\n🌌人设：高智感清冷御姐\\\\n🔥吹爆点：自带松弛感buff/一听就是豪门独女/言情作者都来偷名字！\\\\n#高级感id #起名鬼才 #云朵系穿搭天花板\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"静澜\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"『静』若寒潭映皎月，『澜』起眼底碎星辰！\\\\n🌫️清冷钓系美人名号焊死！\\\\n🌌人设：古风圈天菜cv\\\\n🔥吹爆点：破碎感拿捏绝了/念一次心动一次/小说都不敢写的绝色！\\\\n#起名封神 #氛围感玄学 #清冷系天花板\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"若水\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"『若』有江南三分柔，『水』含灵韵九回肠！\\\\n🌸纯欲风起名界永远的神！\\\\n🌊人设：汉服圈神仙太太\\\\n🔥吹爆点：温柔到滴水的发音/国风起名模版答案/自带水墨画bgm！\\\\n#起名鬼才 #新中式美学 #氛围感玄学\"}}},{\"folderName\":\"封面\",\"generateCount\":\"1\",\"hasSubfolder\":false,\"subfolderName\":\"\",\"textLayerConfigs\":{\"textLayer3\":{\"maxCharsPerLine\":\"6\",\"name\":\"封面\",\"sampleText\":\"在裂缝中生长的光\"}}}],\"baseConfig\":{\"accountName\":\"测试1\",\"psdLocalPath\":\"C:\\\\\\\\Users\\\\\\\\liuxianying\\\\\\\\Downloads\\\\\\\\A4xin.psd\",\"imageSavePath\":\"C:\\\\\\\\output\",\"templateName\":\"测试1\"}}', '0', NULL, '2025-04-24 23:25:17');
INSERT INTO `psd_task` VALUES (170, '7a9342c1-0dbc-4af7-9e0a-32db7e8f11a2', NULL, '测试1', '测试1', 'admin', NULL, '{\"copywriter\":\"\\\\n\\\\n✨「月影下的无声告白」新生儿国风名册大公开！🔥\\\\n\\\\n📚【古风圈妈妈私藏取名攻略】\\\\n1️⃣映雪寒——冬夜寒梅凝霜白\\\\n❄️取自\\\\\"窗含西岭千秋雪\\\\\"意境\\\\n适配清冷系姓氏：江/林/白\\\\n\\\\n2️⃣听风吟——盛夏蝉鸣绕回廊\\\\n🍃灵感源自《诗经》\\\\\"穆如清风\\\\\"\\\\n推荐配温婉姓：苏/叶/温\\\\n\\\\n3️⃣落星尘——银河碎玉坠人间\\\\n🌌出自敦煌壁画星宿图\\\\n适配大气姓：陆/沈/墨\\\\n\\\\n4️⃣墨染秋——水墨丹青点枫笺\\\\n🍁取宋徽宗瘦金体笔意\\\\n诗意搭配：颜/谢/顾\\\\n\\\\n5️⃣疏影深——月移花影上阑干\\\\n🌙化用林逋\\\\\"疏影横斜\\\\\"典故\\\\n适配复姓：慕容/南宫/澹台\\\\n\\\\n💡取名三诀：\\\\n①翻《声律启蒙》找对仗词\\\\n②锁定二十四节气意象\\\\n③虚实字混搭（例：星+尘）\\\\n\\\\n🌺正在待产的姐妹快收藏！评论区留下姓氏，揪5个宝宝定制专属诗意名～\\\\n\\\\n#新生儿取名 #国风名字 #诗词取名 #孕期日记 #宝宝起名攻略\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"映雪寒\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"杨本身是一种树木，拆解为木易二字，结合姓氏字形，音律都为上佳，也有祝福孩子茁壮成长为参天大树，顺风顺水之意。\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"听风吟\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"杨本身是一种树木，拆解为木易二字，结合姓氏字形，音律都为上佳，也有祝福孩子茁壮成长为参天大树，顺风顺水之意。\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"落星尘\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"杨本身是一种树木，拆解为木易二字，结合姓氏字形，音律都为上佳，也有祝福孩子茁壮成长为参天大树，顺风顺水之意。\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"墨染秋\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"杨本身是一种树