INSERT INTO `psd_task` VALUES (171, 'f1613ea1-90ab-4c97-91e4-ad1052c9a95a', NULL, '测试1', '测试1', 'admin', NULL, '{\"copywriter\":\"\\\\n\\\\n🌟在遗忘的岸边拾起星光✨新生儿国风取名全攻略\\\\n\\\\n▫️刷到这篇的准妈妈有福啦！\\\\n最近帮闺蜜挑出生证明名字\\\\n整理出这套「诗意不撞款」起名法则\\\\n附6个神仙冷门备选名👇🏻\\\\n\\\\n💡核心公式：\\\\n【意境元素+虚实叠字+音律平仄】\\\\n示例拆解：\\\\n风烟渺=风烟（自然气象）+渺（缥缈感）\\\\n唇齿碰撞出水墨画般的留白意境\\\\n\\\\n🪄私藏6个绝美名字：\\\\n❶风烟渺丨松烟入墨的疏离感\\\\n雾色晨光里若隐若现的竹林意象\\\\n❷霜华落丨冷月凝霜的清贵气\\\\n取自\\\\\"夜如何其夜未央，庭燎之光\\\\\"\\\\n❸暮云归丨落日熔金的温柔力\\\\n既有黄昏的诗意又不失归途的暖意\\\\n❹秋水长丨自带国画留白基因\\\\n比\\\\\"秋月\\\\\"更显天地辽阔的格局\\\\n❺星河漫丨打破常规天文意象\\\\n比\\\\\"星辰\\\\\"多了银河倾泻的动态美\\\\n❻灵韵天成丨万物有灵的玄妙感\\\\n适配龙宝宝的神秘东方气韵\\\\n\\\\n⚠️避坑指南：\\\\n▫️避免生僻字（系统可能无法录入）\\\\n▫️注意方言发音（比如\\\\\"诗\\\\\"在粤语同\\\\\"尸\\\\\"）\\\\n▫️慎用多音字（容易被读错）\\\\n\\\\n评论区晒晒你家宝贝的名字？\\\\n说不定能触发隐藏款诗意彩蛋✨\\\\n\\\\n（配图建议：手写书法姓名笺+星月夜国画背景）\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"风烟渺\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"杨本身是一种树木，拆解为木易二字，结合姓氏字形，音律都为上佳，也有祝福孩子茁壮成长为参天大树，顺风顺水之意。\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"霜华落\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"杨本身是一种树木，拆解为木易二字，结合姓氏字形，音律都为上佳，也有祝福孩子茁壮成长为参天大树，顺风顺水之意。\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"暮云归\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"杨本身是一种树木，拆解为木易二字，结合姓氏字形，音律都为上佳，也有祝福孩子茁壮成长为参天大树，顺风顺水之意。\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\