INSERT INTO `psd_task` VALUES (172, '81c7aa8f-2d0d-4ecc-83f8-ca2ee24da19b', NULL, '测试1', '测试1', 'admin', NULL, '{\"copywriter\":\"\\\\n\\\\n「时光褶皱里的无声告白」  \\\\n新生儿起名攻略｜藏在古诗词里的绝美名字  \\\\n\\\\n✨ 给宝宝取名是父母的第一封情书  \\\\n这5个自带电影氛围感的名字  \\\\n藏着跨越千年的东方浪漫  \\\\n\\\\n▫️暮雨深——  \\\\n\\\\\"空山新雨后 暮色染林深\\\\\"  \\\\n雨滴在黛瓦上蜿蜒的意象  \\\\n适合生在雨季的温柔系宝宝  \\\\n\\\\n▫️寒江雪——  \\\\n\\\\\"孤舟蓑笠翁 独钓寒江雪\\\\\"  \\\\n清冷破碎感直接拉满  \\\\n自带水墨画卷的留白美学  \\\\n\\\\n▫️烟波渺——  \\\\n\\\\\"日暮乡关何处是 烟波江上使人愁\\\\\"  \\\\n缥缈如纱的朦胧意境  \\\\n念起来唇齿生香的江南韵脚  \\\\n\\\\n▫️月影疏——  \\\\n\\\\\"梧桐更兼细雨 月影婆娑入疏窗\\\\\"  \\\\n清辉流转的月夜意象  \\\\n清冷中带着诗意的温度  \\\\n\\\\n▫️林风晚——  \\\\n\\\\\"松月生夜凉 风泉满清听\\\\\"  \\\\n山林晚风掠过发梢的写意感  \\\\n自由洒脱的森系气质名  \\\\n\\\\n✅ 取名秘籍：  \\\\n1️⃣ 截取古诗意象词组合  \\\\n2️⃣ 姓与名声调错落有致  \\\\n3️⃣ 避免生僻字但保留韵味  \\\\n4️⃣ 结合出生时节/家族文化  \\\\n\\\\n🌸 互动话题 🌸  \\\\n留下你的姓氏+心仪风格  \\\\n揪3位宝贝送定制诗意名解析  \\\\n\\\\n#新生儿取名 #国风名字 #诗词里的名字  \\\\n#孕期日记 #宝宝起名 #冷门绝美名字\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"暮雨深\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"破碎感天花板说的就是你吧！\\\\n自带电影感滤镜的绝美ID，雨天emo文学照进现实，BE美学爱好者原地开嗑！输入法都记住的暗恋系网名，谁用谁是小哭包预定！\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"寒江雪\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"清冷钓系ID直接封神！\\\\n刷屏级氛围感扑面而来，武侠剧白月光名字有脸了！每打一次这个id都像在写诗，当代互联网i人起名模版我疯狂Ctrl+V！\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"烟波渺\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"国风圈新晋顶流id诞生！\\\\n江南水墨画成精现场，古偶大女主名字都不敢这么绝！打游戏用这个id队友秒变夸夸群，自带柔光特效的名字鲨疯了！\"}}},{