INSERT INTO `psd_task` VALUES (173, 'c4d4a02a-c607-4068-9488-142d173346fe', NULL, '测试1', '测试1', 'admin', NULL, '{\"copywriter\":\"\\\\n\\\\n🌊新生儿起名灵感库｜古风圈封神的四个绝美名字\\\\n\\\\n「苍梧谣」\\\\n▫️性别：中性\\\\n▫️风格：武侠秘境x山水诗韵\\\\n▫️出处：《水经注》苍梧之野有仙踪，搭配《诗经》清越谣歌\\\\n✨起名tips：虚词收尾自带韵律感，适合搭配云/墨/砚等水墨意象字\\\\n\\\\n「素月凝」\\\\n▫️性别：女宝\\\\n▫️风格：冷清破碎感x皎月美学\\\\n▫️出处：纳兰容若「辛苦最怜天上月」与李清照「清辉玉臂寒」\\\\n✨起名公式：颜色字+自然意象，同系列可衍生「黛山漪」「青瓷雪」\\\\n\\\\n「浮生烬」\\\\n▫️性别：男宝\\\\n▫️风格：哲思感x新国风\\\\n▫️解读：既有「浮生若梦」的禅意，又含「星火燎原」的燃系内核\\\\n✨进阶玩法：将家长名字拆解重组，比如父姓沈+母名晚，可作「沈烬晚」\\\\n\\\\n「归墟引」\\\\n▫️性别：男宝\\\\n▫️风格：上古神话x异域幻想\\\\n▫️灵感：《山海经》归墟秘境+敦煌飞天图鉴\\\\n✨搭配建议：英文名可延展Quinn/Xavier，与中文名形成奇幻联动\\\\n\\\\n📜取名雷区预警：\\\\n❶避免「梓轩紫萱」等同音爆款\\\\n❷慎用生僻字（如㵘燚等）\\\\n❸注意方言谐音（尤其粤语吴语区）\\\\n\\\\n当潮汐漫过灯塔的倒影\\\\n这些名字里藏着山海永恒的秘语\\\\n评论区交出你收藏的诗意名字吧～\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"苍梧谣\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"素月凝\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"浮生烬\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"归墟引\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"\"}}},{\"folderName\":\"封面\",\"generateCount\":\"1\",\"hasSubfolder\":