INSERT INTO `psd_task` VALUES (174, '45dd3cd6-a996-42d3-bd58-1f9a45d98e30', NULL, '测试1', '测试1', 'admin', NULL, '{\"copywriter\":\"\\n\\n🍼新生儿取名攻略｜被追问800次的国风名字来啦！\\n\\n「时间的褶皱里藏着最温柔的礼物」\\n这些从古诗词里走出来的名字\\n正在等待与新生宝宝相遇✨\\n\\n▫️烟雨遥——「一川烟草梅子黄时雨」\\n取自贺铸《青玉案》的朦胧意象\\n自带江南水墨画的温润感\\n寓意宝宝此生自在逍遥 不困于世俗风雨\\n\\n▫️霜月寒——「晓镜但愁云鬓改 夜吟应觉月光寒」\\n李商隐诗中的清冷美学\\n适合出生在深秋的宝宝\\n藏着父母「守得云开见月明」的期许\\n\\n▫️碧落霞——「上穷碧落下黄泉」\\n白居易笔下最惊艳的天际想象\\n适合灵动活泼的女宝\\n念起来像含着一朵粉紫色晚霞\\n\\n▫️翠微寒——「春山暖日清风 楼阁帘栊啼莺」\\n从「千里横黛色 数峰出云间」化用\\n既有山水画的苍翠\\n又暗含「梅花香自苦寒来」的深意\\n\\n▫️烟岚轻——「瀑布杉松常带雨 夕阳彩翠忽成岚」\\n王维诗中的山林雾气\\n适合气质恬淡的宝宝\\n每个字都带着飘渺仙气\\n\\n🌸取名心经：\\n①避免生僻字但要有典故\\n②声调要错落有致\\n③结合出生时令场景\\n④从父母定情诗里找灵感\\n\\n留言区揪三位准妈妈送「诗经楚辞典藏版」\\n想知道你的姓氏适合配什么古风名？\\n👇🏻欢迎带姓氏互动呀\\n\\n#新生儿取名 #国风名字 #诗词里的浪漫 #孕期日记 #宝宝取名攻略\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"烟雨遥2\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"时间的褶皱：在记忆与遗忘之间的漫游2\"}}},{\"folderName\":\"封面\",\"generateCount\":\"1\",\"hasSubfolder\":false,\"subfolderName\":\"\",\"textLayerConfigs\":{\"textLayer3\":{\"maxCharsPerLine\":\"6\",\"name\":\"封面\",\"sampleText\":\"时间的褶皱：在记忆与遗忘之间的漫游2\"}}}],\"baseConfig\":{\"accountName\":\"测试1\",\"psdLocalPath\":\"C:\\\\Users\\\\liuxianying\\\\Downloads\\\\A4xin.psd\",\"imageSavePath\":\"C:\\\\output\",\"templateName\":\"测试1\"}}', '0', NULL, '2025-04-26 19:39:36');
