INSERT INTO `psd_task` VALUES (181, 'faf94a9a-c184-40ef-9c7e-0fc059096fe5', NULL, '测试1', '测试1', 'admin', NULL, '{\"copywriter\":\"\\\\n\\\\n✨《时间的彼岸｜藏在24节气里的绝美新生儿名》🕰️\\\\n\\\\n🌟【高赞古风名单】\\\\n🍁霜叶红：白露为霜红叶寄相思\\\\n🎋竹影摇：月下君子风骨自成\\\\n🕊️雁南渡：鸿雁传书寄千里情思\\\\n🌲松间月：空山新雨后的禅意美学\\\\n💧泉声幽：枕水听音的治愈系美名\\\\n\\\\n✅新手爸妈取名攻略：\\\\n1️⃣节气灵感库：蒹葭/谷雨/小满等字眼自带诗意滤镜\\\\n2️⃣五感联动法：颜色（黛/墨）+动态（拂/鸣）+意境（远/幽）\\\\n3️⃣诗词截取术：从「晚来天欲雪」取「晚雪」\\\\n4️⃣自然混搭学：植物（棠/芷）+天文（星/云）碰撞出新国风\\\\n\\\\n💡小贴士：\\\\n👉🏻避开高频字但保留辨识度\\\\n👉🏻注意方言发音忌讳\\\\n👉🏻避免笔画复杂的生僻字\\\\n\\\\n「你更想给宝宝哪个季节的名字呢？」\\\\n欢迎孕妈们在↓交流你们的取名灵感💌\\\\n\\\\n#宝宝取名 #国风名字 #新生儿 #孕期日记 #文化传承\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"霜叶红\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"霜叶红｜秋天的绝绝子！\\\\n\\\\n红叶与白霜的碰撞就是神仙配色！这名字一听就是古风圈天菜，氛围感直接拉满，随手一拍都是电影截图级别，建议所有国风游戏都按这个标准卷起来！\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"竹影摇\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"竹影摇｜顶级破碎感代名词！\\\\n\\\\n光影美学巅峰之作！明明可以叫普通竹影偏要加个摇字，清冷中透着破碎，BE美学DNA狠狠动了，放在小说里起码是白月光女配配置！\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"雁南渡\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"雁南渡｜迁徙界天花板实锤！\\\\n\\\\n把候鸟迁徙说得这么诗情画意也是没谁了！建议申遗的国风意境，听到就想写小作文的程度，文旅宣传片没它我不看！\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"松间月\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"松间月｜氛围感界扛把子！\\\\n\\\\n王维看了都要说6的取名艺术！月光穿透松针的丁达尔效应被古人玩明白了，清冷破碎感直接封神，摄影博主现在拍还来得及蹭热度！\"}}},{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"5\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"泉声幽\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"泉声幽｜ASMR界初代顶流！\\\\n\\\\n这才是真正的白噪音鼻祖！建议做成3D环绕音效助眠，什么流水鸟叫都弱爆了，听到「泉声幽」三个字耳膜已经开始自动马杀鸡了！\"}}},{\"folderName\":\"封面\",\"generateCount\":\"1\",\"hasSubfolder\":false,\"subfolderName\":\"\",\"textLayerConfigs\":{\"textLayer3\":{\"maxCharsPerLine\":\"6\",\"name\":\"封面\",\"sampleText\":\"时间的彼岸\"}}}],\"baseConfig\":{\"accountName\":\"测试1\",\"psdLocalPath\":\"C:\\\\\\\\Users\\\\\\\\liuxianying\\\\\\\\Downloads\\\\\\\\A4xin.psd\",\"imageSavePath\":\"C:\\\\\\\\output\",\"templateName\":\"测试1\"}}', '0', NULL, '2025-05-01 00:07:47');
