INSERT INTO `psd_task` VALUES (183, 'cade0d7a-eec9-464a-aeba-3f406b034f9b', NULL, '测试1', '测试1', 'admin', NULL, '{\"copywriter\":\"\\\\n\\\\n【绝了！新生儿取名还能这样玩？】⏳✨\\\\n\\\\n《时间沙漏中的无声告白》\\\\n——静痕语の出生哲学\\\\n\\\\n给宝宝取名的N个心机㊙️\\\\n1️⃣【静痕语】拆解指南\\\\n√静：取《诗经》\\\\\"静言思之\\\\\"的澄澈感\\\\n√痕：时光镌刻的独家印记\\\\n√语：宋词\\\\\"欲语泪先流\\\\\"的深情载体\\\\n——当岁月流淌过沙漏，这个名字会替孩子诉说你们未宣之于口的爱意\\\\n\\\\n2️⃣ 三大取名法则\\\\n▫️留白艺术：名字要有呼吸感（如\\\\\"痕\\\\\"字自带故事张力）\\\\n▫️矛盾美学：静与语的碰撞像月光与潮汐\\\\n▫️时空胶囊：把父母人生的重要时刻藏进笔画（沙漏/告白意象）\\\\n\\\\n3️⃣ 私藏取名灵感库\\\\n✓参考张爱玲式意象：琉璃瓦/沉香屑\\\\n✓截取电影台词：《情书》里的\\\\\"你好吗\\\\\"\\\\n✓自然现象编码：用\\\\\"霰/岚/霁\\\\\"记出生当天的天气\\\\n\\\\n评论区晒出你家宝宝的「人生初代ID」\\\\n揪三位送独家定制版《诗经解名手札》📜\\\\n\\\\n#新生儿取名 #高级感名字 #诗经起名 #孕妈必备 #宝宝成长日记\",\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"generateCount\":\"1\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"maxCharsPerLine\":\"3\",\"name\":\"名字\",\"sampleText\":\"静痕语\"},\"textLayer2\":{\"maxCharsPerLine\":\"15\",\"name\":\"正文\",\"sampleText\":\"\"}}},{\"folderName\":\"封面\",\"generateCount\":\"1\",\"hasSubfolder\":false,\"subfolderName\":\"\",\"textLayerConfigs\":{\"textLayer3\":{\"maxCharsPerLine\":\"6\",\"name\":\"封面\",\"sampleText\":\"《时间沙漏中的无声告白》\"}}}],\"baseConfig\":{\"accountName\":\"测试1\",\"psdLocalPath\":\"C:\\\\\\\\Users\\\\\\\\liuxianying\\\\\\\\Downloads\\\\\\\\A4xin.psd\",\"imageSavePath\":\"C:\\\\\\\\output\",\"templateName\":\"测试1\"}}', '0', 'C:\\output\\2025-05-01\\admin\\测试1_测试1_13-54-36', '2025-05-01 13:54:36');
INSERT INTO `psd_template` VALUES (2, '{\"copywriterPrompt\":\"请为小红书平台创作一篇新生儿取名攻略类爆款文案，要求如下：\\\\n\\\\n🌟 内容结构：\\\\n1️⃣ 吸睛标题：用【】框住核心主题，加入「新手爸妈/2024趋势/避坑指南」等关键词\\\\n2️⃣ 痛点共鸣：列举3个家长取名常见焦虑（重名率高/风格混乱/文化底蕴不足）\\\\n3️⃣ 解决方案：提炼5种人气风格公式（国风意象派、极简新中式、自然治愈系等） \\\\n4️⃣ 风格拆解：每个风格=取名逻辑+适配姓氏+避雷提示（搭配🌿✨⚡️等场景化emoji）\\\\n5️⃣ 互动钩子：引导收藏模版/留言姓氏定制/投票选择偏好风格\\\\n\\\\n🎨 文案要求：\\\\n✅ 口语化表达：使用\\\\\\\"抄作业/锁死/避大雷\\\\\\\"等年轻化词汇\\\\n✅ 视觉分层：每段用💡🌟👉等emoji引导阅读节奏\\\\n✅ 数据背书：融入「2024热搜字Top10」「重名率低于0.1%」等权威标签\\\\n✅ 场景联想：搭配「出生证明/周岁宴/学名小名」等使用场景\\\\n\\\\n📝 示例框架：\\\\n【2024取名开挂指南】这些神仙组合让名字像小说主角！\\\\n💔 新手爸妈三大崩溃瞬间：翻遍诗经重名率99%→精心设计被说像偶像剧...\\\\n✨ 破局公式：自然流+姓氏谐音=全网0重复！（配古风手写名卡图）\\\\n🌰 案例：李姓→李眠溪（诗意）/ 陈姓→陈允墨（书卷气） \\\\n👉 戳合集get《20大姓氏适配词库》❤️留下姓氏送你3组高定方案！\",\"prompt\":\"textLayer1是名字，textLayer2是名字描述\\n给我生成运动风格名字，需来源于热门运动。\\n结合网络流行语，生成富有吸引力的 名字解析 & 吹爆式描述，符合小红书热文风格，适当使用 换行符 \\\\r 让排版更佳。\",\"baseConfig\":{\"templateName\":\"测试\",\"accountName\":\"测试\",\"psdLocalPath\":\"C:\\\\Users\\\\11254\\\\Downloads\\\\A4xin.psd\",\"imageSavePath\":\"C:\\\\output\"},\"imageConfigs\":[{\"folderName\":\"正文靠左 三个字 无姓氏\",\"hasSubfolder\":true,\"subfolderName\":\"4\",\"textLayerConfigs\":{\"textLayer1\":{\"name\":\"名字\",\"sampleText\":\"远之\",\"maxCharsPerLine\":3,\"prompt\":\"\"},\"textLayer2\":{\"name\":\"正文\",\"sampleText\":\"杨本身是一种树木，拆解为木易二字，结合姓氏字形，音律都为上佳，也有祝福孩子茁壮成长为参天大树，顺风顺水之意。 \",\"maxCharsPerLine\":15}}}]}', '/profile/upload/2025/03/30/微信图片_20240922041856_20250330133942A001.jpg,/profile/upload/2025/03/30/微信图片_20250227101412_20250330133942A002.jpg,/profile/upload/2025/03/30/微信图片_20250224185241_20250330133943A003.jpg');
