INSERT INTO `sys_oper_log` VALUES (461, 'psd任务', 2, 'com.ruoyi.web.controller.custom.PsdTaskController.checkTask()', 'POST', 1, 'admin', '研发部门', '/psd/task/checkTask', '127.0.0.1', '内网IP', '{\"accountName\":\"测试1\",\"config\":\"{\\\"copywriter\\\":\\\"\\\\n【新生儿取名绝了！这个自带书香的名字居然出自朱熹名句】\\\\n\\\\n✨《观书有感》里的神仙名字被我挖到了！  \\\\n朱熹笔下的\\\\\\\"问渠那得清如许\\\\\\\"  \\\\n藏着这个自带书卷气的绝美三字名——  \\\\n「清如许」  \\\\n\\\\n▫️出处硬核：宋代大儒朱熹经典名句  \\\\n▫️音韵清越：三字平仄相间朗朗上口  \\\\n▫️适配百搭：配苏/林/江等姓氏都超惊艳  \\\\n▫️意境满分：既有流水清澈的灵动感  \\\\n又暗含\\\\\\\"活水源头\\\\\\\"的智慧哲学  \\\\n▫️性别通用：男女宝宝都能用的中性美名  \\\\n\\\\n❤️敲黑板划重点：  \\\\n1️⃣避开生僻字又能彰显文化底蕴  \\\\n2️⃣三字结构平衡不撞款  \\\\n3️⃣适配现代80/90后父母审美  \\\\n\\\\n最近在《典籍里的中国》又听到这句诗  \\\\n突然发现用作人名简直封神！  \\\\n配古风汉服照或现代日常装都超有氛围  \\\\n\\\\n↓ ↓ ↓  \\\\n正在纠结宝宝名字的准爸妈快收好  \\\\n这个把江南水乡装进名字里的神仙配置  \\\\n自带国学滤镜还能写进族谱！  \\\\n\\\\n#新生儿取名 #诗词取名 #宝宝起名攻略  \\\\n#清如许 #国学经典  \\\\n\\\\n（评论区晒出你家崽的神仙名字，揪三位送手写诗词名帖～）\\\",\\\"imageConfigs\\\":[{\\\"folderName\\\":\\\"正文靠左 三个字 无姓氏\\\",\\\"generateCount\\\":\\\"1\\\",\\\"hasSubfolder\\\":true,\\\"subfolderName\\\":\\\"4\\\",\\\"textLayerConfigs\\\":{\\\"textLayer1\\\":{\\\"maxCharsPerLine\\\":\\\"3\\\",\\\"name\\\":\\\"名字\\\",\\\"sampleText\\\":\\\"清如许1\\\"},\\\"textLayer2\\\":{\\\"maxCharsPerLine\\\":\\\"15\\\",\\\"name\\\":\\\"正文\\\",\\\"sampleText\\\":\\\"【清如许】名字解析｜宋韵国风DNA动了\\\\n◽️取自朱熹『问渠那得清如许』的诗意\\\\n◽️自带清泉流响的山水画氛围感\\\\n◽️比奶茶三分糖更清冽的高级感\\\"}}},{\\\"folderName\\\":\\\"封面\\\",\\\"generateCount\\\":\\\"1\\\",\\\"hasSubfolder\\\":false,\\\"subfolderName\\\":\\\"\\\",\\\"textLayerConfigs\\\":{\\\"textLayer3\\\":{\\\"maxCharsPerLine\\\":\\\"6\\\",\\\"name\\\":\\\"封面\\\",\\\"sampleText\\\":\\\"时光褶皱里的未寄信笺2\\\"}}}],\\\"baseConfig\\\":{\\\"accountName\\\":\\\"测试1\\\",\\\"psdLocalPath\\\":\\\"C:\\\\\\\\Users\\\\\\\\liuxianying\\\\\\\\Downloads\\\\\\\\A4xin.psd\\\",\\\"imageSavePath\\\":\\\"C:\\\\\\\\output\\\",\\\"templateName\\\":\\\"测试1\\\"}}\",\"createDate\":\"2025-04-16 10:31:55\",\"id\":160,\"params\":{},\"status\":\"2\",\"templateName\":\"测试1\",\"uuid\":\"6764988b-5058-